function isDigit(k){ return /^[0-9]$/.test(k); }
function appendChar(k){
  if(k === '.'){
    if(cur.includes('.')) return;
    cur = (cur === '0') ? '0.' : (cur + '.');
    return;
  }
  if(isDigit(k)){
    cur = (cur === '0') ? k : (cur + k);
  }
}

function compute(x, op, y){
  const A = Number(x), B = Number(y);
  if(op === '+') return A + B;
  if(op === '-') return A - B;
  if(op === '*') return A * B;
  if(op === '/'){
    if(B === 0) return 'Error';
    return A / B;
  }
  return B;
}

